mapper.select:
		SELECT * FROM health_heart_rate
		WHERE user_id = #{uId}
		AND dates IN

            #{item.dates}

		SET @dates := null;
        UPDATE health_heart_rate



					when dates=#{item.dates} then #{item.times}




					when dates=#{item.dates} then #{item.results}



        WHERE user_id=#{uId} AND dates IN

            #{item.dates}

        AND (SELECT @dates := CONCAT_WS(',', dates, @dates));
        SELECT @dates;

SELECT times,results,dates FROM health_heart_rate
WHERE user_id = #{userId} AND dates >= #{dates}
ORDER BY dates;
